import React, { Component } from 'react';
import { RiSearchLine } from 'react-icons/ri';

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showSearchIcon: true,
      popularIsActive: false,
      recentIsActive: false,
      isActive: false,
    };
  }
  onInputClick = () => {
    this.setState({ showSearchIcon: false });
  };

  onPopularClick = () => {
    this.setState({ popularIsActive: !this.state.popularIsActive });
  };
  onRecentClick = () => {
    this.setState({ recentIsActive: !this.state.recentIsActive });
  };

  // onButtonClick = () => {
  //   this.setState({ isActive: !this.state.isActive });
  // };

  render() {
    const popularIsActive = this.state.popularIsActive;
    const recentIsActive = this.state.recentIsActive;
    const isActive = this.state.isActive;
    return (
      <div className="Search">
        <input
          type="text"
          value={this.props.term}
          onChange={this.props.onInputChange}
          onKeyPress={this.props.onInputChange}
          onClick={this.onInputClick}
          placeholder="Search portfolios by school, major or year"
        />
        {this.state.showSearchIcon ? (
          <RiSearchLine size="1.5em" className="Icon" />
        ) : null}

        <div className="SearchItemBox">
          <button
            onClick={() => {
              this.props.sortByMostPopular();
              this.onPopularClick();
              // this.onButtonClick();
            }}
            className={`${popularIsActive ? 'isActive' : ''}`}
            // className={`${isActive ? 'isActive' : ''}`}
          >
            Most Popular
          </button>
          <button
            // className={`${recentIsActive ? 'inActive' : ''}`}
            className={`${isActive ? 'isActive' : ''}`}
            onClick={() => {
              this.props.sortByMostRecent();
              this.onRecentClick();
              // this.onButtonClick();
            }}
          >
            Most Recent
          </button>
        </div>
      </div>
    );
  }
}
export default Search;
