import './App.css';
import React, { Component } from 'react';
import Nav from './components/Nav';
import Search from './components/Search';
import PortfolioCard from './components/PortfolioCard';
import portfolioDB from './DB/portfolioDB';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: null,
      mainImg: '',
      name: '',
      school: '',
      major: '',
      year: 2020,
      portfolio: [{ img: [], desc: '' }],
      portfolioData: [],
      term: '',
    };
  }

  componentDidMount() {
    this.setState({ portfolioData: portfolioDB });
  }
  onTermSubmit = (term, searchTerm) => {
    let filterSchool = portfolioDB;
    console.log(filterSchool);
    filterSchool = filterSchool.filter((portfolio) => {
      return portfolio.school.toLowerCase().search(term.toLowerCase()) !== -1;
    });

    this.setState({ portfolioData: filterSchool, term: searchTerm });
  };

  onInputChange = (e) => {
    this.setState({ term: e.target.value });
  };
  render() {
    return (
      <div className="App">
        <Nav />
        <Search
          onTermSubmit={this.onTermSubmit}
          term={this.state.term}
          onInputChange={this.onInputChange}
        />
        <div className="Card-grid-wrapper">
          {this.state.portfolioData.map((portfolio) => (
            <PortfolioCard portfolio={portfolio} />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
