import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Nav from './components/Nav';
import Search from './components/Search';
import PortfolioCard from './components/PortfolioCard';
import portfolioDB from './DB/portfolioDB';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: null,
      mainImg:
        'https://images.unsplash.com/photo-1551732998-9573f695fdbb?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1650&q=80',
      name: 'Chance Lipshutz',
      school: 'California College of the Arts',
      major: 'Illustration',
      year: 2020,
      Portfolio: [{ img: [], desc: '' }],
    };
  }

  render() {
    return (
      <div className="App">
        <Nav />
        <Search />
        <div className="Card-grid-wrapper">
          {portfolioDB.map((portfolio) => (
            <PortfolioCard portfolio={portfolio} />
          ))}
          <PortfolioCard
            className="Card-grid"
            mainImg={this.state.mainImg}
            school={this.state.school}
            major={this.state.major}
            userName={this.state.name}
            year={this.state.year}
          />
        </div>
      </div>
    );
  }
}

export default App;
