import React, { Component } from 'react';
import { RiSearchLine } from 'react-icons/ri';

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showSearchIcon: true,
      buttonActive: '',
    };
    this.updateActiveButton = this.updateActiveButton.bind(this);
  }
  onInputClick = () => {
    this.setState({ showSearchIcon: false });
  };

  updateActiveButton(active) {
    this.setState(function () {
      return {
        buttonActive: active,
      };
    });
  }

  render() {
    const buttonActive = this.state.buttonActive;
    return (
      <div className="Search">
        <input
          type="text"
          value={this.props.term}
          onChange={this.props.onInputChange}
          onKeyPress={this.props.onInputChange}
          onClick={this.onInputClick}
          placeholder="Search portfolios by school, major or year"
        />
        {this.state.showSearchIcon ? (
          <RiSearchLine size="1.5em" className="Icon" />
        ) : null}

        <div className="SearchItemBox">
          <button
            onClick={() => {
              this.props.sortByMostPopular();
              this.updateActiveButton();
            }}
            className={buttonActive ? 'isActive' : null}
          >
            Most Popular
          </button>

          <button
            className={buttonActive ? 'isActive' : null}
            onClick={() => {
              this.props.sortByMostRecent();
              this.updateActiveButton();
            }}
          >
            Most Recent
          </button>
        </div>
      </div>
    );
  }
}
export default Search;
