import React, { Component } from 'react';
import { RiSearchLine } from 'react-icons/ri';
import portfolioDB from './DB/portfolioDB';

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      term: '',
      showSearchIcon: true,
    };
  }

  onInputChange = (e) => {
    this.setState({ term: e.target.value });
    console.log(e.target.value);
  };

  onInputSubmit = (e) => {
    if (e.key === 'Enter') {
      console.log(this.state.term);
    }
    this.props.onTermSubmit(this.state.term);
  };

  onInputClick = () => {
    this.setState({ showSearchIcon: false });
  };

  render() {
    return (
      <div className="Search">
        <input
          type="text"
          value={this.state.term}
          onChange={this.onInputChange}
          onKeyPress={this.onInputSubmit}
          // onSubmit={this.onInputSubmit}
          onClick={this.onInputClick}
        />
        {this.state.showSearchIcon ? (
          <RiSearchLine size="1.5em" className="Icon" />
        ) : null}
        <div className="SearchItemBox">
          <button className="Active">School</button>
          <button>Major</button>
          <button>Year</button>
        </div>
      </div>
    );
  }
}

export default Search;

