import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Nav from './components/Nav';
import Search from './components/Search';
import PortfolioCard from './components/PortfolioCard';
import portfolioDB from './DB/portfolioDB';

class App extends Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     id: null,
  //     mainImg:
  //       'https://images.unsplash.com/photo-1551732998-9573f695fdbb?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1650&q=80',
  //     name: 'Chance Lipshutz',
  //     school: 'California College of the Arts',
  //     major: 'Illustration',
  //     year: 2020,
  //     Portfolio: [{ img: [], desc: '' }],
  //   };
  // }

  render() {
    const portfolioDB = [
      {
        id: 346364,
        mainImg:
          'https://images.unsplash.com/photo-1594445863100-9cbf9d747e1a?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1651&q=80',
        school: 'California College of the Arts',
        major: 'Illustration',
        name: 'Chance Lipshutz',
        Year: 2020,
        Portfolio: [{ img: [], desc: '' }],
      },
      {
        id: 346365,
        mainImg:
          'https://images.unsplash.com/photo-1551732998-9573f695fdbb?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1650&q=80',
        school: 'New York University',
        major: 'Fine Arts',
        name: 'Allison Lubin',
        Year: 2021,
        Portfolio: [{ img: [], desc: '' }],
      },
      {
        id: 346366,
        mainImg:
          'https://images.unsplash.com/photo-1526304760382-3591d3840148?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1650&q=80',
        school: 'University of Michigan',
        major: 'Design',
        name: 'Ann Carder',
        Year: 2019,
        Portfolio: [{ img: [], desc: '' }],
      },
    ];
    return (
      <div className="App">
        <Nav />
        <Search />
        <div className="Card-grid-wrapper">
          {portfolioDB.map((portfolio) => (
            <PortfolioCard portfolio={portfolio} />
          ))}
          <PortfolioCard
            className="Card-grid"
            mainImg={this.state.mainImg}
            school={this.state.school}
            major={this.state.major}
            userName={this.state.name}
            year={this.state.year}
          />
        </div>
      </div>
    );
  }
}

export default App;
